def generate_paths(tree, path=''):
    if not tree:
        return [path]
    paths = []
    for key, subtree in tree.items():
        new_path = f'{path}/{key}' if path else key
        paths += generate_paths(subtree, new_path)
    return paths if path else paths[1:]

