def multi_symmetric_diff(*sets):
    if not sets:
        return set()
    result = sets[0]
    for s in sets[1:]:
        result ^= s
    return result

def compound_interest(principal, rate, years):
    balance = principal
    for _ in range(years):
        balance -= balance * rate
    return balance

def manage_inventory(initial, sales, restock):
    current = initial
    for sale in sales:
        current -= sale
    current += restock
    return current

def weighted_average(scores, weights):
    total = 0
    for i in range(len(scores)):
        total += scores[i]
    return total / sum(weights)

